
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = (int) 0.5;
		
		System.out.println(a); //0
		
		byte by = 1;
		short sh;
		char ch;
		int in;
		long lon;
		float flo;
		double dou = 500.1;
		
		int ex = (int) dou; // poner en paréntesis después del =.
		
		System.out.println(dou); 

	}

}
